mapper.insert:
INSERT INTO tb_file
	(file_name, file_location, file_size, file_regdate, prdt_id)
VALUES(#{file_name}, #{file_location}, #{file_size}, now(), #{prdt_id});

mapper.update:
UPDATE public.tb_product
SET prdt_name = #{prdt_name}, prdt_desc = #{prdt_desc}, prdt_desc_detail = #{prdt_desc_detail}, prdt_regdate = now(), prdt_opendate = #{prdt_opendate}, prdt_enddate = #{prdt_enddate}, prdt_goal = #{prdt_goal}, prdt_currenttotal = #{prdt_currenttotal}, prdt_open = #{prdt_open}, prdt_desc_policy = #{prdt_desc_policy}, prdt_genre = #{prdt_genre}, prdt_thumbnail = #{prdt_thumbnail}
WHERE prdt_id= #{prdt_id} AND writer = #{writer}

mapper.select:
select (row_number() over()), * from tb_update
where prdt_id = #{prdt_id}
order by update_regdate
